import React, {Component} from 'react'
import Cards from './components/Cards';
import Nav from './components/Nav';
import './App.css';

const apiKey= "e971cf7f9b0bfd0a24087a5b3c3bbaa7";

export default class App extends Component {
  constructor (props){
    super(props)
    this.state={
      cities: []
    };
  }
  onSearch (city){
    fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city},{state code}&appid=${apiKey}`)
      .then (r => r.json())
      .then ((receiver) => {
        if (receiver.main !== undefined){
          const city = {
            temp_min: Math.round(receiver.main.temp_min),
            temp_max: Math.round(receiver.main.temp_max),
            description:receiver.weather[0].description,
            img:receiver.weather[0].icon,
            humidity:receiver.main.humidity,
            weather:receiver.weather[0].main,
            clouds:receiver.clouds.all,
            id:receiver.id,
            name:receiver.name
          };
          this.setState ({cities:[...this.state.cities, city]}) // al estado anterior le agrego la nueva ciudad
        } else {
          alert ("City not found");
        }
      });
  }
  onClose (id) {
    this.setState({cities:this.cities.filter(c=>c.id !==id)}); // elimino del estado actual la ciudad seleccionada
  }
  render (){
  return (
    <div className="App">
      <Nav onSearch={this.onSearch}/>
      <Cards cities={this.cities} onClose={this.onClose}/>
    </div>
  );
}
}
